import React from 'react'
import {Card, Row, Col, Modal} from 'antd'

export default class Gallery extends React.Component {
  state = {
    visible: false
  }
  openGallery = (imgSrc) => {
    this.setState({
      visible: true,
      currentImg: imgSrc
    })
  }
  render() {
    const imgs = [
      ['1.png','2.png','3.png','4.png','5.png'],
      ['6.png','7.png','8.png','9.png','10.png'],
      ['11.png','12.png','13.png','14.png','15.png'],
      ['16.png','17.png','18.png','19.png','20.png'],
      ['21.png','22.png','23.png','24.png','25.png'],
    ]
    const imgList = imgs.map((list) =>  <div> <Row gutter={16}>
      {list.map((img) => (
        <Col md={4}>
      <Card 
        style={{marginBottom:10}}
        cover={<img src={'/gallery/' + img}/>}
        onClick={() => this.openGallery('/gallery/' + img)}
      >
        <Card.Meta 
          title="React Admin"
          description="love React"
        />
      </Card></Col>
      ))}</Row></div>)
    return(
      <div className="card-warp">
        {imgList}
        <Modal 
          height={300}
          width={300}
          title={"图片画廊"}
          visible={this.state.visible}
          onCancel={() => {
            this.setState({
              visible: false
            })
          }}
          footer={null}
        >
          <img style={{height:250,width:150}} src={this.state.currentImg} />
        </Modal>
      </div>
    )
  }
}
