import React from 'react' 
import {Card, Table, Modal, Button, message, Badge} from 'antd'
import Axios from './../../axios/index.js'
import Utils from './../../utils/utils.js';

export default class BasicTable extends React.Component {
  state = {

  }
  params = {
    page:1
  }
  componentDidMount() {
    this.request()
  }
  request = () => {
    let _this = this
    Axios.ajax({
      url:'/table/high/list',
      data: {
        params: {
          page:this.params.page
        },
        
      }
    }).then((res) => {
      if(res.code == 0) {
        this.setState({
          dataSource:res.result.list.map((item,index) => {
            item.key = index
            return item
          }),
        })
      }
    })
  }
  handleChange = (pagination, filters, sorter ) => {
    this.setState({
      sortOrder: sorter.order
    })
  }
  // 删除操作
  handleDelete = (item) => {
    let id = item.id
    Modal.confirm({
      title: '确认',
      content: '确认删除吗',
      onOk: () => {
        message.success('删除成功')
        this.request()
      }
    })
  }
  render() {
    const columns = [
      {
        title:'id',
        dataIndex: 'id',
        width:70,
      },
      {
        title:'用户名',
        width:70,
        dataIndex: 'userName'
      },
      {
        title:'性别',
        width:70,
        dataIndex: 'sex',
        render(sex){
          return sex === 1 ? '男' : '女'
        }
      },
      {
        title:'状态',
        width:70,
        dataIndex: 'state',
        render(state) {
          let config = {
            "1": '初中',
            "2": '高中',
            "3": '大学',
            "4": '硕士',
            "5": '博士',
          }
          return config[state]
        }
      },
      {
        title:'爱好',
        width:70,
        dataIndex: 'interest',
        render(interest) {
          let config = {
            "1": '爬山',
            "2": '篮球',
            "3": '足球',
            "4": '排球',
            "5": '游泳',
            "6": '旅行',
            "7": '唱歌',
            "8": '看书',
          }
          return config[interest]
        }
      },
      {
        title:'生日',
        width:120,
        dataIndex: 'birthday'
      },
      {
        title:'地址',
        width:120,
        dataIndex: 'address'
      },
      {
        title:'早起时间',
        width:80,
        dataIndex: 'time'
      },
    ]
    const columns2 = [
      {
        title:'id',
        dataIndex: 'id',
        width:70,
      },
      {
        title:'用户名',
        width:70,
        dataIndex: 'userName'
      },
      {
        title:'性别',
        width:70,
        dataIndex: 'sex',
        render(sex){
          return sex === 1 ? '男' : '女'
        }
      },
      {
        title:'年龄',
        width:70,
        dataIndex: 'age',
        sorter:(a,b) => {
          return a.age - b.age
        },
        sortOrder: this.state.sortOrder
      },
      {
        title:'状态',
        width:70,
        dataIndex: 'state',
        render(state) {
          let config = {
            "1": '初中',
            "2": '高中',
            "3": '大学',
            "4": '硕士',
            "5": '博士',
          }
          return config[state]
        }
      },
      {
        title:'爱好',
        width:70,
        dataIndex: 'interest',
        render(interest) {
          let config = {
            "1": '爬山',
            "2": '篮球',
            "3": '足球',
            "4": '排球',
            "5": '游泳',
            "6": '旅行',
            "7": '唱歌',
            "8": '看书',
          }
          return config[interest]
        }
      },
      {
        title:'生日',
        width:120,
        dataIndex: 'birthday'
      },
      {
        title:'地址',
        width:120,
        dataIndex: 'address'
      },
      {
        title:'早起时间',
        width:80,
        dataIndex: 'time'
      },
    ]
    const columns3 = [
      {
        title:'id',
        dataIndex: 'id',
        width:70,
      },
      {
        title:'用户名',
        width:70,
        dataIndex: 'userName'
      },
      {
        title:'性别',
        width:70,
        dataIndex: 'sex',
        render(sex){
          return sex === 1 ? '男' : '女'
        }
      },
      {
        title:'年龄',
        width:70,
        dataIndex: 'age',
      },
      {
        title:'状态',
        width:70,
        dataIndex: 'state',
        render(state) {
          let config = {
            "1": '初中',
            "2": '高中',
            "3": '大学',
            "4": '硕士',
            "5": '博士',
          }
          return config[state]
        }
      },
      {
        title:'爱好',
        width:70,
        dataIndex: 'interest',
        render(interest) {
          let config = {
            "1": <Badge status="success" text="成功"/>,
            "2": <Badge status="error" text="报错"/>,
            "3": <Badge status="default" text="正常"/>,
            "4": <Badge status="processing" text="进行中"/>,
            "5": <Badge status="warning" text="警告"/>,
          }
          return config[interest]
        }
      },
      {
        title:'生日',
        width:120,
        dataIndex: 'birthday'
      },
      {
        title:'地址',
        width:120,
        dataIndex: 'address'
      },
      {
        title:'操作',
        render: (text,item) => {
          return <Button size="small" onClick={(item) => {this.handleDelete(item)}}>删除</Button>
        }
      },
    ]
    return(
      <div>
        <Card title="头部固定">
          <Table 
            bordered
            columns={columns}
            dataSource={this.state.dataSource}
            pagination={false}
            scroll={{y:100}}
          />
        </Card>
        <Card title="表格排序">
          <Table 
            bordered
            columns={columns2}
            dataSource={this.state.dataSource}
            pagination={false}
            onChange={this.handleChange}
          />
        </Card>
        <Card title="操作按钮">
          <Table 
            bordered
            columns={columns3}
            dataSource={this.state.dataSource}
            pagination={false}
          />
        </Card>
      </div>
    )
  }
}
