import React from 'react'
import {Card, Form, Input, Button, Checkbox, Radio, Select, Switch, DatePicker, TimePicker, Upload, Icon, message, InputNumber} from 'antd'
import moment from 'moment'

const FormItem = Form.Item
const RadioGroup = Radio.Group
const {Option} = Select
const {TextArea} = Input
class FormReg extends React.Component {
  state = {}
  getBase64 = (img, callback) => {
    const reader = new FileReader();
    reader.addEventListener('load', () => callback(reader.result));
    reader.readAsDataURL(img);
  }
  handleChange = info => {
    if (info.file.status === 'uploading') {
      this.setState({ loading: true });
      return;
    }
    if (info.file.status === 'done') {
      // Get this url from response in real world.
      this.getBase64(info.file.originFileObj, imageUrl =>
        this.setState({
          userImg:imageUrl,
          loading: false,
        }),
      );
    }
  };
  handleSubmit = () => {
    let userInfo = this.props.form.getFieldsValue()
    console.log(JSON.stringify(userInfo))
    message.success('欢迎')
  }
  render() {
    const {getFieldDecorator} = this.props.form 
    const formItemLayout = {
      labelCol: {
        xs: 24,
        sm: 4,
      },
      wrapperCol: {
        xs: 24,
        sm: 12,
      }
    }
    const offsetLayout = {
      wrapperCol: {
        xs:24,
        sm: {
          span: 12,
          offset: 4,
        }
      }
    }
    return (
      <div> 
        <Card title="注册表单">
          <Form>
            <FormItem label="用户名" {...formItemLayout}>
              {
                getFieldDecorator('userName',{
                  initialValue: '',
                  rules: [
                    {
                      required: true,
                      message: '用户名不能为空'
                    },
                  ]
                })(<Input placeholder="请输入用户名"/>)
              }
            </FormItem>
            <FormItem label="密码" {...formItemLayout}>
              {
                getFieldDecorator('userPwd',{
                  initialValue: '',
                  rules: [
                    {
                      required: true,
                    }
                  ]
                })(<Input type="password" placeholder="请输入密码"/>)
              }
            </FormItem>
            <FormItem label="性别" {...formItemLayout}>
              {
                getFieldDecorator('sex',{
                  initialValue: '1',
                  rules: [
                    
                  ]
                })(
                  <RadioGroup>
                    <Radio value="1">男</Radio>
                    <Radio value="2">女</Radio>
                  </RadioGroup>
                )
              }
            </FormItem>
            <FormItem label="年龄" {...formItemLayout}>
              {
                getFieldDecorator('age',{
                  initialValue: 18,
                  rules: [
                    
                  ]
                })(
                  <InputNumber />
                )
              }
            </FormItem>
            <FormItem label="当前状态" {...formItemLayout}>
              {
                getFieldDecorator('state',{
                  initialValue: '1',
                  rules: [
                    
                  ]
                })(
                  <Select>
                    <Option value="1">初中</Option>
                    <Option value="2">高中</Option>
                    <Option value="3">大学</Option>
                    <Option value="4">硕士</Option>
                    <Option value="5">博士</Option>
                  </Select>
                )
              }
            </FormItem>
            <FormItem label="爱好" {...formItemLayout}>
              {
                getFieldDecorator('like',{
                  initialValue: ['1','2','3'],
                  rules: [
                    
                  ]
                })(
                  <Select mode="multiple">
                    <Option value="1">篮球</Option>
                    <Option value="2">跑步</Option>
                    <Option value="3">唱歌</Option>
                    <Option value="4">游泳</Option>
                    <Option value="5">写代码</Option>
                    <Option value="6">乒乓</Option>
                    <Option value="7">足球</Option>
                    <Option value="8">排球</Option>
                  </Select>
                )
              }
            </FormItem>
            <FormItem label="是否已婚" {...formItemLayout}>
              {
                getFieldDecorator('isMarried',{
                  valuePropName: 'checked',
                  initialValue: true,
                  rules: [
                    
                  ]
                })(
                  <Switch />
                )
              }
            </FormItem>
            <FormItem label="生日" {...formItemLayout}>
              {
                getFieldDecorator('birthday',{
                  initialValue: moment('2019-05-20'),
                  rules: [
                    
                  ]
                })(
                  <DatePicker 
                    showTime
                    format="YYYY-MM-DD"
                  />
                )
              }
            </FormItem>
            <FormItem label="联系地址" {...formItemLayout}>
              {
                getFieldDecorator('address',{
                  initialValue: '北京市南山南',
                  rules: [
                    
                  ]
                })(
                  <TextArea autosize={{
                    minRows:2,
                    maxRows:6
                  }}></TextArea>
                )
              }
            </FormItem>
            <FormItem label="早起时间" {...formItemLayout}>
              {
                getFieldDecorator('time',{
                  initialValue:'' ,
                  rules: [
                    
                  ]
                })(
                  <TimePicker showTime ></TimePicker>
                )
              }
            </FormItem>
            <FormItem label="头像" {...formItemLayout}>
              {
                getFieldDecorator('userImg')(
                  <Upload listType="picture-card" 
                    showUploadList={false}
                    action="https://www.mocky.io/v2/5cc8019d300000980a055e76"
                    onChange={this.handleChange}
                  >
                    {this.state.userImg ? <img src={this.state.userImg} /> : <Icon type="plus" />}
                  </Upload>
                )
              }
            </FormItem>
            <FormItem {...offsetLayout} labelCol>
              {
                getFieldDecorator('xieyi')(
                  <Checkbox>我已阅读<a href="#">相关协议</a></Checkbox>
                )
              }
            </FormItem>
            <FormItem {...offsetLayout} >
              <Button type="primary" onClick={this.handleSubmit}>注册</Button>
            </FormItem>
          </Form>
        </Card>
      </div>
    )
  }
}
export default Form.create()(FormReg)
